  labelCol: {
    xs: { span: 24 },
    sm: { span: 5 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 12 }
  }
};
const edit01 = props => {
  useEffect(() => {
    console.log("MODAL !!", props);
  }, []);

  const { setEditVisible, gubun } = props;
  const inputDisabled = gubun === "N" ? false : true;
  return (
    <>
      <button onClick={() => setEditVisible(false)}> 뒤로가기 </button>
      <Form {...formItemLayout}>
        <Form.Item label="내용" style={{ fontSize: 200 }} />
        <Form.Item label="제목">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="기안자">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="업무">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="기안일">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="문서 번호">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="내용">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="비고">
          <Input disabled={inputDisabled} />
        </Form.Item>
      </Form>
      {/* <Form>
        <Form.item label="abc">
          <Input disabled={false} />
        </Form.item>
      </Form> */}
    </>
  );
};

export default edit01;
