import React, { useState } from "react";
import { Form, Icon, Input, Button, Checkbox } from "antd";

const edocLogin = props => {
  // hooks state
  // const [state명, state변경함수] = useState("default값")
  const useInput = (initValue = null) => {
    const [value, setter] = useState(initValue);
    const handler = e => {
      setter(e.target.value);
    };
    return [value, handler];
  };
  const [id, setId] = useInput("");
  const [password, setPassword] = useState("");

  // event
  const onChagneId = e => {
    // state변경함수에 넘어오는 parm (e) value 값을 해준다.
    // react에서 render되는 조건은 state가 변경되었을때마다
    setId(e.target.value);
  };
  const onChagnePw = e => {
    setPassword(e.target.value);
  };

  const onSubmit = e => {
    e.preventDefault();
    console.log("loginbtn Clicked", id, password);
  };
  return (
    <div style={{ padding: 20 }}>
      <Form className="login-form" onSubmit={onSubmit}>
        <div>
          <Input
            prefix={<Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />}
            placeholder="ID"
            required
            onChange={onChagneId} // input 은 항상 value랑 onchange가 짝이 되야 한다
            name="id"
            value={id}
          />
        </div>
        <div>
          <Input
            prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
            placeholder="PASSWORD"
            required
            onChange={useInput}
            name="password"
            value={password}
          />
        </div>
        <div>
          {/* <button type="submit" > 로그인 </button> 을 antd 입히면 아래와 같이 htmlType 으로 해줘야한다. */}
          <Button type="primary" htmlType="submit">
            로그인
          </Button>
        </div>
      </Form>
    </div>
  );
};

export default edocLogin;
