import React, { useState, useEffect } from "react";
import { Table, Input, Button, Icon } from "antd";
import Highlighter from "react-highlight-words";
import { useSelector } from "react-redux";
import { LIST_EDEG1CODE } from "../../../globalValue";
const edoc01 = () => {
  const [searchText, setSerchText] = useState("");
  const { edoc } = seSelector(state => state.edocument);
  const doc =
    edoc !== null &&
    edoc.filter(item => item.EDEG1Code >= 100 && item.EDEG1Code < 200);

  var searchInput = null;
  const getColumnSearchProps = dataIndex => ({
    filterDropdown: ({
      setSelectedKeys,
      selectedKeys,
      confirm,
      clearFilters
    }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={node => {
            searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={e =>
            setSelectedKeys(e.target.value ? [e.target.value] : [])
          }
          onPressEnter={() => handleSearch(selectedKeys, confirm)}
          style={{ width: 188, marginBottom: 8, display: "block" }}
        />
        <Button
          type="primary"
          onClick={() => handleSearch(selectedKeys, confirm)}
          icon="search"
          size="small"
          style={{ width: 90, marginRight: 8 }}
        >
          Search
        </Button>
        <Button
          onClick={() => handleReset(clearFilters)}
          size="small"
          style={{ width: 90 }}
        >
          Reset
        </Button>
      </div>
    ),
    filterIcon: filtered => (
      <Icon type="search" style={{ color: filtered ? "#1890ff" : undefined }} />
    ),
    onFilter: (value, record) =>
      record[dataIndex]
        .toString()
        .toLowerCase()
        .includes(value.toLowerCase()),
    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => searchInput.select());
      }
    },
    render: text => (
      <Highlighter
        highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
        searchWords={[searchText]}
        autoEscape
        textToHighlight={text.toString()}
      />
    )
  });
  const handleSearch = (selectedKeys, confirm) => {
    confirm();
    setSerchText(selectedKeys[0]);
  };

  const handleReset = clearFilters => {
    clearFilters();
    setSerchText("");
  };

  const filteredInfo = filteredInfo || {};
  const filtedEdeg1code = LIST_EDEG1CODE.map((item, index) => {
    return {
      text: item.name,
      value: item.value
    };
  });
  const columns = [
    {
      title: "구분",
      dataIndex: "EDEG2Code",
      key: "EDEG2Code",
      width: "10%",
      filters: filtedEdeg1code.filter(
        item => item.value === 100 || item.value === 101 || item.value === 102
      ),
      filteredValue: filteredInfo.name || null,
      onFilter: (value, record) => record.name.includes(value)
    },
    {
      title: "제목",
      dataIndex: "EDTitle",
      key: "EDTitle",
      width: "50%",
      ...getColumnSearchProps("EDTitle")
    },
    {
      title: "기안일",
      dataIndex: "EDDateTime",
      key: "EDDateTime",
      width: "20%"
    },
    {
      title: "결재자",
      dataIndex: "EJAFTJOINSTNAME",
      key: "EJAFTJOINSTNAME",
      width: "10%",
      ...getColumnSearchProps("address")
    },
    {
      title: "상태",
      dataIndex: "EJSIGNGUBUN",
      key: "EJSIGNGUBUN",
      width: "10%"
    }
  ];
  return <Table columns={columns} dataSource={doc} />;
  // return <div />;
};

export default edoc01;
