import { Table, Input, Button, Icon, Tabs } from "antd";
import Highlighter from "react-highlight-words";

// 검색기능
var searchInput = null;
export const getColumnSearchProps = (dataIndex, searchText, setSerchText) => ({
  filterDropdown: ({
    setSelectedKeys,
    selectedKeys,
    confirm,
    clearFilters
  }) => (
    <div style={{ padding: 8 }}>
      <Input
        ref={node => {
          searchInput = node;
        }}
        placeholder={`Search ${dataIndex}`}
        value={selectedKeys[0]}
        onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
        onPressEnter={() => handleSearch(selectedKeys, confirm, setSerchText)}
        style={{ width: 188, marginBottom: 8, display: "block" }}
      />
      <Button
        type="primary"
        onClick={() => handleSearch(selectedKeys, confirm, setSerchText)}
        icon="search"
        size="small"
        style={{ width: 90, marginRight: 8 }}
      >
        Search
      </Button>
      <Button
        onClick={() => handleReset(clearFilters, setSerchText)}
        size="small"
        style={{ width: 90 }}
      >
        Reset
      </Button>
    </div>
  ),
  filterIcon: filtered => (
    <Icon type="search" style={{ color: filtered ? "#1890ff" : undefined }} />
  ),
  onFilter: (value, record) =>
    record[dataIndex]
      .toString()
      .toLowerCase()
      .includes(value.toLowerCase()),
  onFilterDropdownVisibleChange: visible => {
    if (visible) {
      setTimeout(() => searchInput.select());
    }
  },
  render: text => (
    <Highlighter
      highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
      searchWords={[searchText]}
      autoEscape
      textToHighlight={text.toString()}
    />
  )
});
export const handleSearch = (selectedKeys, confirm, setSerchText) => {
  confirm();
  setSerchText(selectedKeys[0]);
};

export const handleReset = (clearFilters, setSerchText) => {
  clearFilters();
  setSerchText("");
};

// 필터 기능
export const handleChange = (pagination, filters, sorter) => {
  // console.log("Various parameters", pagination, filters, sorter);
  setFilterdInfo(filters);
  setSortedInfo(sorter);
  // this.setState({
  //   filteredInfo: filters,
  //   sortedInfo: sorter
  // });
};
