import React, { useState } from "react";
import { Modal, Button } from "antd";

const edit01 = props => {
  console.log(props);
  const { visible, setVisible, isLoading, setIsLoading } = props;
  const handleOk = () => {
    this.setState({ isLoading: true });
    setTimeout(() => {
      setIsLoading(false), setVisible(false);
    }, 3000);
  };

  const handleCancel = () => {
    setVisible(false);
  };

  return (
    <Modal
      visible={visible}
      title="Title"
      onOk={handleOk}
      onCancel={handleCancel}
      footer={[
        <Button key="back" onClick={handleCancel}>
          Return
        </Button>,
        <Button
          key="submit"
          type="primary"
          loading={isLoading}
          onClick={handleOk}
        >
          Submit
        </Button>
      ]}
    >
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
    </Modal>
  );
};

export default edit01;
