import React, { useState, useEffect } from "react";
import { Modal, Button } from "antd";

const edit01 = props => {
  const [isLoading, setIsLoading] = useState(false);
  const { visible, setVisible, clickedItem } = props;
  const handleOk = () => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false), setVisible(false);
    }, 3000);
  };

  useEffect(() => {
    if (props.visible) {
      console.log("MODAL !!", props);
    }
  });
  const handleCancel = () => {
    setVisible(false);
  };

  return (
    <Modal
      visible={visible}
      title="Title"
      onOk={handleOk}
      onCancel={handleCancel}
      footer={[
        <Button key="back" onClick={handleCancel}>
          Return
        </Button>,
        <Button
          key="submit"
          type="primary"
          loading={isLoading}
          onClick={handleOk}
        >
          Submit
        </Button>
      ]}
    >
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
    </Modal>
  );
};

export default edit01;
