//user 정보를 담고있는 스토어, 초기값
export const initialState = {
  isLoggedIn: false,
  user: {}
};

export const LOG_IN = "LOG_IN"; // 액션의 이름
export const LOG_OUT = "LOG_OUT";
export const LOG_IN_SUCCESS = "LOG_OUT";
export const LOG_IN_FAILURE = "LOG_OUT";

const loginAction = {
  type: LOG_IN,
  data: {
    nickname: "JW"
  }
};
const logoutAction = {
  type: LOG_OUT
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case LOG_IN: {
      return {
        // 다음 state 변경
        ...state,
        isLoggedIn: true,
        user: action.data
      };
    }
    case LOG_OUT: {
      return {
        ...state,
        isLoggedIn: false,
        user: {}
      };
    }
    default: {
      return {
        ...state
      };
    }
  }
};
// 리액트와 연결전에 미리 이런 작업을 해두어야 한다.

export default reducer;
