import React, { useSelector, useEffect } from "react";
import { Form, Input, Button } from "antd";
import axios from "axios";
const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 2 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 20 }
  }
};
const edit01 = props => {
  useEffect(() => {
    axios.post("/edoc/getEdoc/detail");
  }, []);

  const { setEditVisible, gubun } = props;
  const inputDisabled = gubun === "N" ? false : true;
  return (
    <div>
      <Form {...formItemLayout}>
        <Form.Item
          wrapperCol={{
            xs: { span: 24, offset: 13 },
            sm: { span: 24, offset: 20 }
          }}
        >
          <Button onClick={() => setEditVisible(false)}> 취소</Button>
          <Button
            type="primary"
            htmlType="submit"
            onClick={() => setEditVisible(false)}
          >
            저장
          </Button>
        </Form.Item>
        <label style={{ fontSize: 30 }}>내용</label>
        <Form.Item label="제목">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="기안자">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="업무">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="기안일">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="문서 번호">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="내용">
          <Input.TextArea rows={10} disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="비고">
          <Input disabled={inputDisabled} />
        </Form.Item>
        <label style={{ fontSize: 30 }}>결재 처리</label>
        <Form.Item label="결재자">
          <Input disabled={inputDisabled} />
        </Form.Item>

        <Form.Item label="결재자 의견">
          <Input disabled={inputDisabled} />
        </Form.Item>
      </Form>
    </div>
  );
};

export default edit01;
