import React, { useState, useEffect } from "react";
import { Modal, Button } from "antd";

const edit01 = props => {
  const [isLoading, setIsLoading] = useState(false);
  const { visible, setVisible, clickedItem } = props;
  const handleOk = () => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false), setVisible(false);
    }, 3000);
  };

  useEffect(() => {
    console.log("MODAL !!", props);
  }, [visible]);

  const handleCancel = () => {
    setVisible(false);
  };

  return (
    <>
      <div>EDIT</div>
    </>
  );
};

export default edit01;
