import React, { useState } from "react";
import { Modal, Button } from "antd";

const edit01 = props => {
  const { visible, setVisible, isLoading, setIsLoading } = this.props;
  handleOk = () => {
    this.setState({ isLoading: true });
    setTimeout(() => {
      setIsLoading(false), setVisible(false);
    }, 3000);
  };

  handleCancel = () => {
    setVisible(false);
  };

  return (
    <Modal
      visible={visible}
      title="Title"
      onOk={this.handleOk}
      onCancel={this.handleCancel}
      footer={[
        <Button key="back" onClick={this.handleCancel}>
          Return
        </Button>,
        <Button
          key="submit"
          type="primary"
          loading={loading}
          onClick={this.handleOk}
        >
          Submit
        </Button>
      ]}
    >
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
    </Modal>
  );
};

export default edit01;
